import config from '../../Utils/config'

const { channel,icon_emoji,text,url,username} = config.platforms.slack

function slackNotification() {
  fetch(url, {
    method: "POST",

    body: {
      channel,
      username,
      text,
      icon_emoji,
    }
  })
  .then(json => console.log(json))
}

export default slackNotification
