import React,{useState} from 'react';

import './styles.css'
import ViewItem from '../ViewItem/index'

function Section() {
    const [repo, setRepo] = useState('')

    function handleViewItem(name) {
        setRepo(name)
        document.getElementById("containerViewItem").style.display = "flex"
    }

    return (
        <>
        <div className="containerSection">
            <ul id="containerNavigationSection">
                <li> ALL</li>
                <li><i class="fas fa-window-maximize"></i> WEB</li>
                <li><i class="fab fa-android"></i> ANDROID</li>
                <li><i class="fab fa-apple"></i>IOS</li>
                <li><i class="fas fa-desktop"></i>DESKTOP</li>
            </ul>
            <div id="containerProjects">
                <img id="covid19-brazil-api" src="https://github.com/devarthurribeiro/covid19-brazil-api/raw/master/static/screenshot.png" alt="picture image" onClick={()=> handleViewItem("SMUH")} />
            </div>
        </div>
        </>
    )
}

export default Section;
