import React, {useEffect, useState} from 'react'
import Markdown from 'markdown-to-jsx'

import './styles.css'




export default function ViewItem({repository}){
    const [content, setContent] = useState('#')

    useEffect(()=>{
        console.log(repository)
        try {
            fetch(`https://raw.githubusercontent.com/jadson179/{}/master/README.md`)
            .then(response => response.text())
            .then(text => setContent(text))
        } catch (error) {
            console.log(error)
        }
    },[])

    return (
        <div className="containerViewItem" id="containerViewItem">
            <Markdown id="renderMarkdown">
                { content }
            </Markdown>
        </div>
    )
}
