import React, { useState } from 'react';
import { __web,__android,__desktop,__ios ,__all} from '../../Utils/repositories'

import './styles.css'





function Section() {
    const [type, setType] = useState(__all.map(item => (item)))

    return (
        <>
            <div className="containerSection">
                <ul id="containerNavigationSection">
                    <li onClick={()=> setType(__all.map(item => (item)))} > ALL</li>
                    <li onClick={()=> setType(__web.map(item => (item)))} ><i className="fas fa-window-maximize"></i> WEB</li>
                    <li onClick={()=> setType(__android.map(item => (item)))} ><i className="fab fa-android"></i> ANDROID</li>
                    <li onClick={()=> setType(__ios.map(item => (item)))} ><i className="fab fa-apple"></i>IOS</li>
                    <li onClick={()=> setType(__desktop.map(item => (item)))} ><i className="fas fa-desktop"></i>DESKTOP</li>
                </ul>
                <div id="containerProjects">
                    {
                        type?.map(item => (
                            <a href={item.url}><img src={item.img} alt="picture image" /></a>
                        ))
                    }
                </div>
            </div>
        </>
    )
}

export default Section;
